#include <iostream>
using namespace std;

bool ispar(string x)
{
    // Your code here
    if (x.length() & 1)
    {
        return false;
    }
    else
    {
        string str = "";
        for (int i = 0; i < x.length(); i++)
        {
            if (x[i] == '{')
            {
                str.push_back('}');
            }
            else if (x[i] == '[')
            {
                str.push_back(']');
            }
            else if (x[i] == '(')
            {
                str.push_back(')');
            }

            else
            {
                if (x[i] == str[str.length() - 1])
                {
                    str.pop_back();
                }
                else
                {
                    return false;
                }
            }
        }
        if (str.length() != 0)
        {
            return false;
        }
        else
        {

            return true;
        }
    }
}

